mapper.select:
SELECT ${sql_fields}
FROM ${sql_tablename}


		fid = #{fid}


		AND street_ = #{street_}


		AND community_ = #{community_}


		AND origin_address = #{origin_address}


		AND similar_address = #{similar_address}


		AND standard_address = #{standard_address}


		AND status = #{status}


		AND modifier = #{modifier}


		AND update_date = #{update_date}



	ORDER BY ${sql_orderBy}


	LIMIT #{sql_limit}

