SELECT ${sql_fields}
FROM ${sql_tablename}


		id = #{id}


		province = #{province}


		AND city = #{city}


		AND district = #{district}


		AND street = #{street}


		AND community = #{community}


		AND address = #{address}


		AND address_id = #{address_id}


		AND building = #{building}


		AND building_id = #{building_id}


		AND code = #{code}


		AND road = #{road}


		AND road_num = #{road_num}


		AND village = #{village}



	ORDER BY ${sql_orderBy}


	LIMIT #{sql_limit}

